import React, { useState, useEffect } from "react";
import { getCatFact, getGif } from "../services/api";

const CurrentResult = () => {
  const [fact, setFact] = useState("");
  const [gifUrl, setGifUrl] = useState("");

  useEffect(() => {
    fetchCatFact();
  }, []);

  const fetchCatFact = async () => {
    const fact = await getCatFact();
    setFact(fact);

    const query = fact.split(" ").slice(0, 3).join(" ");
    const gif = await getGif(query);
    setGifUrl(gif);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      <div className="flex items-center space-x-8">
        <div className="w-1/2">
          {gifUrl ? (
            <img src={gifUrl} alt="GIF relacionado" className="rounded-lg shadow-lg" />
          ) : (
            <p>Sin GIF disponible</p>
          )}
        </div>
        <div className="w-1/2 text-center">
          <h2 className="text-2xl font-bold mb-4">Cat Fact</h2>
          <p className="text-lg">{fact}</p>
          <button
            className="mt-4 bg-blue-500 text-white px-4 py-2 rounded"
            onClick={fetchCatFact}
          >
            Refrescar GIF
          </button>
        </div>
      </div>
    </div>
  );
};

export default CurrentResult;
