import React, { useState, useEffect } from "react";
import { getHistory } from "../../services/api";

const HistoryTable = () => {
  const [history, setHistory] = useState([]);
  const [search, setSearch] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = async () => {
    const data = await getHistory();
    const sortedData = data.sort((a, b) => new Date(b.searchDate) - new Date(a.searchDate));
    setHistory(sortedData);
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
    setCurrentPage(1);
  };

  const filteredHistory = history.filter(
    (item) =>
      item.catFact.toLowerCase().includes(search.toLowerCase()) ||
      item.query.toLowerCase().includes(search.toLowerCase())
  );

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredHistory.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredHistory.length / itemsPerPage);

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-4xl mx-auto mt-8">
      <h2 className="text-2xl font-bold mb-4 text-gray-700">Historial de BÃºsquedas</h2>
      <input
        type="text"
        placeholder="Buscar..."
        className="border p-2 rounded mb-4 w-full"
        value={search}
        onChange={handleSearch}
      />

      <table className="w-full bg-gray-100 rounded-lg">
        <thead>
          <tr className="bg-gray-300 text-gray-700">
            <th className="py-2 px-4">Fecha</th>
            <th className="py-2 px-4">Cat Fact</th>
            <th className="py-2 px-4">Query</th>
            <th className="py-2 px-4">GIF URL</th>
          </tr>
        </thead>
        <tbody>
          {currentItems.length === 0 ? (
            <tr>
              <td colSpan="4" className="text-center py-4">No hay historial disponible</td>
            </tr>
          ) : (
            currentItems.map((item) => (
              <tr key={item.id} className="border-b hover:bg-gray-50">
                <td className="py-2 px-4">{new Date(item.searchDate).toLocaleString()}</td>
                <td className="py-2 px-4">{item.catFact}</td>
                <td className="py-2 px-4">{item.query}</td>
                <td className="py-2 px-4">
                  {item.gifUrl ? (
                    <a href={item.gifUrl} target="_blank" rel="noopener noreferrer" className="text-blue-500 underline">
                      Ver GIF
                    </a>
                  ) : (
                    "No disponible"
                  )}
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default HistoryTable;
