import React, { useState, useEffect } from "react";
import { getCatFact, getGif } from "../services/api";
import "../App.css";

const CurrentResult = () => {
  const [fact, setFact] = useState("");
  const [gifUrl, setGifUrl] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchCatFact();
  }, []);

  const fetchCatFact = async () => {
    setLoading(true);
    const fact = await getCatFact();
    setFact(fact);
    const query = fact.split(" ").slice(0, 3).join(" ");
    const gif = await getGif(query);
    setGifUrl(gif);
    setLoading(false);
  };

  const refreshGif = async () => {
    setLoading(true);
    const query = fact.split(" ").slice(0, 3).join(" ");
    const gif = await getGif(query);
    setGifUrl(gif);
    setLoading(false);
  };

  return (
    <div className="flex bg-white p-6 rounded-lg shadow-lg w-3/4 max-w-3xl space-x-8">
      <div className="w-1/2 flex justify-center items-center">
        {loading ? (
          <p className="text-lg text-gray-500">Cargando...</p>
        ) : gifUrl ? (
          <img src={gifUrl} alt="GIF" className="rounded-lg shadow-lg max-h-64" />
        ) : (
          <p>No hay GIF disponible</p>
        )}
      </div>
      <div className="w-1/2 flex flex-col justify-center items-center">
        <h2 className="text-2xl font-bold mb-4 text-gray-700">Cat Fact</h2>
        <p className="text-lg text-gray-700 mb-4">{fact}</p>
        <button
          className="bg-blue-600 text-white px-4 py-2 rounded-lg shadow-lg hover:bg-blue-700"
          onClick={refreshGif}
        >
          Refrescar GIF
        </button>
      </div>
    </div>
  );
};

export default CurrentResult;
